function onSubmit() {
   alert("1  -- " + g_form.getValue('u_application_name'));
	alert("2  -- " + g_form.getValue('u_service_component_name.u_sdm'));
	//var servcomp = g_form.getReference('u_service_component_name', doAlert); // doAlert is our callback function


	var sysidval = g_form.getValue('u_service_component_name.u_sdm');
	var sercomp = new GlideRecord('x_58872_needit_servicecomponents');
	var sdmname ;

	sercomp.addQuery('sys_id');
	sercomp.query();

	while(sercomp.next()){
		if(sercomp.sys_id == sysidval ){
			//gs.info('SDM Name is ' + sercomp.getValue('u_sdm'));
			sdmname = sercomp.getValue('u_sdm');
		}
	}

	alert("sdm name = " + sdmname); 
	//var servcomp = g_form.getReference('u_application_name', doAlert); // doAlert is our callback function
} 


